import React, { useState } from 'react';

const TeamForm = ({ addTeams }) => {
  const [teams, setTeams] = useState([
    { teamName: 'Ceros', win: 0, lost: 0, points: 0 },
    { teamName: 'Panthers', win: 0, lost: 0, points: 0 }
  ]);

  const handleTeamChange = (index, field, value) => {
    const updatedTeams = [...teams];
    updatedTeams[index][field] = value;
    setTeams(updatedTeams);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addTeams(teams);
  };

  return (
    <form onSubmit={handleSubmit}>
      {teams.map((team, index) => (
        <div key={index}>
          <h3>Team {index + 1}</h3>
          <div>
            <label>
              Team Name:
              <input
                type="text"
                value={team.teamName}
                onChange={(e) => handleTeamChange(index, 'teamName', e.target.value)}
              />
            </label>
          </div>
          <div>
            <label>
              Win:
              <input
                type="number"
                value={team.win}
                onChange={(e) => handleTeamChange(index, 'win', parseInt(e.target.value))}
              />
            </label>
          </div>
          <div>
            <label>
              Lost:
              <input
                type="number"
                value={team.lost}
                onChange={(e) => handleTeamChange(index, 'lost', parseInt(e.target.value))}
              />
            </label>
          </div>
          <div>
            <label>
              Points:
              <input
                type="number"
                value={team.points}
                onChange={(e) => handleTeamChange(index, 'points', parseInt(e.target.value))}
              />
            </label>
          </div>
        </div>
      ))}
      <button type="submit">Save Teams</button>
    </form>
  );
};

export default TeamForm;
